CREATE TABLE IF NOT EXISTS Language ( id INTEGER UNSIGNED NOT NULL AUTO_INCREMENT UNIQUE PRIMARY KEY, languageId VARCHAR(5) NOT NULL UNIQUE) ENGINE = InnoDB, CHARACTER SET = utf8, COLLATE = utf8_unicode_ci;
CREATE TABLE IF NOT EXISTS User ( id INTEGER UNSIGNED NOT NULL AUTO_INCREMENT UNIQUE PRIMARY KEY, username VARCHAR(100) NOT NULL UNIQUE, password VARCHAR(100) NOT NULL) ENGINE = InnoDB, CHARACTER SET = utf8, COLLATE = utf8_unicode_ci;
CREATE TABLE IF NOT EXISTS Theme ( id INTEGER UNSIGNED NOT NULL AUTO_INCREMENT UNIQUE PRIMARY KEY, name VARCHAR(100) NOT NULL, context VARCHAR(100) NOT NULL, CONSTRAINT uq_THEME_name_context UNIQUE uq_THEME_name_context (name, context)) ENGINE = InnoDB, CHARACTER SET = utf8, COLLATE = utf8_unicode_ci;
CREATE TABLE IF NOT EXISTS LayoutSet ( id INTEGER UNSIGNED NOT NULL AUTO_INCREMENT UNIQUE PRIMARY KEY, virtualHost VARCHAR(100) NOT NULL UNIQUE, themeId INTEGER UNSIGNED, CONSTRAINT fk_LAYOUTSET_themeId_theme_id FOREIGN KEY fk_LAYOUTSET_themeId_theme_id (themeId) REFERENCES Theme(id) ON DELETE SET NULL ON UPDATE SET NULL) ENGINE = InnoDB, CHARACTER SET = utf8, COLLATE = utf8_unicode_ci;
CREATE TABLE IF NOT EXISTS Layout ( id INTEGER UNSIGNED NOT NULL AUTO_INCREMENT UNIQUE PRIMARY KEY, layoutSetId INTEGER UNSIGNED NOT NULL, friendlyUrl VARCHAR(100) NOT NULL, themeId INTEGER UNSIGNED, pageLayoutId VARCHAR(100) NOT NULL, nr INTEGER UNSIGNED NOT NULL DEFAULT 1, parentId INTEGER UNSIGNED DEFAULT NULL, defaultLayoutTitleLanguageId INTEGER UNSIGNED NOT NULL, CONSTRAINT fk_LAYOUT_parentId_layout_id FOREIGN KEY fk_LAYOUT_parentId_layout_id (parentId) REFERENCES Layout (id) ON DELETE CASCADE ON UPDATE CASCADE, CONSTRAINT fk_LAYOUT_layoutSetId_layoutSet_id FOREIGN KEY fk_LAYOUT_layoutSetId_layoutSet_id (layoutSetId) REFERENCES LayoutSet(id) ON DELETE CASCADE ON UPDATE CASCADE, CONSTRAINT fk_LAYOUT_themeId_theme_id FOREIGN KEY fk_LAYOUT_themeId_theme_id (themeId) REFERENCES Theme(id) ON DELETE SET NULL ON UPDATE SET NULL, CONSTRAINT fk_LAYOUT_dltlid_language_id FOREIGN KEY fk_LAYOUT_dltlid_language_id(defaultLayoutTitleLanguageId) REFERENCES Language(id) ON DELETE CASCADE ON UPDATE CASCADE, CONSTRAINT uq_LAYOUT_parentId_nr UNIQUE uq_LAYOUT_parentId_nr (parentId, nr), CONSTRAINT uq_LAYOUT_layoutSetId_friendlyUrl UNIQUE uq_LAYOUT_layoutSetId_friendlyUrl (layoutSetId, friendlyUrl)) ENGINE = InnoDB, CHARACTER SET = utf8, COLLATE = utf8_unicode_ci;
CREATE TABLE IF NOT EXISTS LayoutTitle ( id INTEGER UNSIGNED NOT NULL AUTO_INCREMENT UNIQUE PRIMARY KEY, languageId INTEGER UNSIGNED NOT NULL, layoutId INTEGER UNSIGNED NOT NULL, title VARCHAR(100) NOT NULL, CONSTRAINT fk_lt_lid_language_id FOREIGN KEY fk_lt_lid_language_id (languageId) REFERENCES Language (id) ON DELETE RESTRICT ON UPDATE CASCADE, CONSTRAINT fk_lt_layoutId_l_id FOREIGN KEY fk_lt_layoutId_l_id (layoutId) REFERENCES Layout (id) ON DELETE CASCADE ON UPDATE CASCADE, CONSTRAINT uq_lt_layoutId_languageId UNIQUE uq_lt_layoutId_languageId (layoutId, languageId)) ENGINE = InnoDB, CHARACTER SET = utf8, COLLATE = utf8_unicode_ci;
CREATE TABLE IF NOT EXISTS PortletInstance ( id INTEGER UNSIGNED NOT NULL AUTO_INCREMENT UNIQUE PRIMARY KEY, portletContext VARCHAR(100) NOT NULL, portletName VARCHAR(100) NOT NULL, windowID VARCHAR(10) NOT NULL, CONSTRAINT uq_PI_portletContext_portletName_windowID UNIQUE uq_PI_portletContext_portletName_windowID (windowID, portletName, portletContext)) ENGINE = InnoDB, CHARACTER SET = utf8, COLLATE = utf8_unicode_ci;
CREATE TABLE IF NOT EXISTS LayoutPortlet ( id INTEGER UNSIGNED NOT NULL AUTO_INCREMENT UNIQUE PRIMARY KEY, layoutId INTEGER UNSIGNED NOT NULL, rowId INTEGER NOT NULL, portletInstanceId INTEGER UNSIGNED NOT NULL, CONSTRAINT uq_LP_layoutId_rowId UNIQUE uq_LP_layoutId_rowId (layoutId, rowId), CONSTRAINT fk_LP_layoutId_layout_id FOREIGN KEY fk_LP_layoutId_layout_id (layoutId) REFERENCES Layout (id) ON DELETE CASCADE ON UPDATE CASCADE, CONSTRAINT fk_LP_portletInstanceId_portletInstance_id FOREIGN KEY fk_LP_portletInstanceId_portletInstance_id (portletInstanceId) REFERENCES PortletInstance (id) ON DELETE CASCADE ON UPDATE CASCADE) ENGINE = InnoDB, CHARACTER SET = utf8, COLLATE = utf8_unicode_ci;
CREATE TABLE IF NOT EXISTS PortletPreference ( id INTEGER UNSIGNED NOT NULL AUTO_INCREMENT UNIQUE PRIMARY KEY, preferenceName VARCHAR(100) NOT NULL, portletInstanceId INTEGER UNSIGNED NOT NULL, CONSTRAINT uq_pp_portlet_instanceId_preferenceName UNIQUE uq_pp_portlet_instanceId_preferenceName (portletInstanceId, preferenceName), CONSTRAINT fk_PP_portletInstanceId_portletInstance_id FOREIGN KEY fk_PP_portletInstanceId_portletInstance_id (portletInstanceId) REFERENCES PortletInstance (id) ON DELETE CASCADE ON UPDATE CASCADE) ENGINE = InnoDB, CHARACTER SET = utf8, COLLATE = utf8_unicode_ci;
CREATE TABLE IF NOT EXISTS PortletPreferenceValue ( id INTEGER UNSIGNED NOT NULL AUTO_INCREMENT UNIQUE PRIMARY KEY, portletPreferenceId INTEGER UNSIGNED NOT NULL, preferenceValue VARCHAR(1024) NOT NULL, CONSTRAINT fk_PPV_portletPreferenceId_portletPreference_id FOREIGN KEY fk_PPV_portletPreferenceId_portletPreference_id (portletPreferenceId) REFERENCES PortletPreference (id) ON DELETE CASCADE ON UPDATE CASCADE) ENGINE = InnoDB, CHARACTER SET = utf8, COLLATE = utf8_unicode_ci;

CREATE TABLE IF NOT EXISTS Resource ( id INT UNSIGNED NOT NULL AUTO_INCREMENT UNIQUE PRIMARY KEY, resource VARCHAR(128) NOT NULL UNIQUE ) ENGINE = InnoDB, CHARACTER SET = utf8, COLLATE = utf8_unicode_ci;
CREATE TABLE IF NOT EXISTS ResourceAction ( id INT UNSIGNED NOT NULL AUTO_INCREMENT UNIQUE PRIMARY KEY, resourceId INT UNSIGNED NOT NULL, action VARCHAR(32) NOT NULL, permission INT UNSIGNED NOT NULL, CONSTRAINT fk_ra_rarid_resource_id FOREIGN KEY (resourceId) REFERENCES Resource (id) ON DELETE CASCADE ON UPDATE CASCADE, CONSTRAINT uq_resourceaction_rid_a_p UNIQUE uq_resourceaction_rid_a_p (resourceId, action, permission) ) ENGINE = InnoDB, CHARACTER SET = utf8, COLLATE = utf8_unicode_ci;
CREATE TABLE IF NOT EXISTS ResourceInstance ( id INT UNSIGNED NOT NULL AUTO_INCREMENT UNIQUE PRIMARY KEY, resourceId INT UNSIGNED NOT NULL, resourcePrimKey INT UNSIGNED NOT NULL, CONSTRAINT fk_resourceinstance_rid_resource_id FOREIGN KEY (resourceId) REFERENCES Resource (id) ON DELETE CASCADE ON UPDATE CASCADE, CONSTRAINT uq_resourceinstance_rid_rprimKey UNIQUE uq_resourceinstance_rid_rprimKey (resourcePrimKey, resourceId) ) ENGINE = InnoDB, CHARACTER SET = utf8, COLLATE = utf8_unicode_ci;
CREATE TABLE IF NOT EXISTS ResourceInstance_Resource ( id INT UNSIGNED NOT NULL AUTO_INCREMENT UNIQUE PRIMARY KEY, resourceInstanceId INT UNSIGNED NOT NULL, resourceId INT UNSIGNED NOT NULL, CONSTRAINT fk_resourceinstanceresource_rid_resource_id FOREIGN KEY (resourceId) REFERENCES Resource (id) ON DELETE CASCADE ON UPDATE CASCADE, CONSTRAINT fk_resourceinstanceresource_riid_resource_id FOREIGN KEY (resourceInstanceId) REFERENCES ResourceInstance (id) ON DELETE CASCADE ON UPDATE CASCADE, CONSTRAINT uq_resourceinstance_rid_rprimKey UNIQUE uq_resourceinstance_rid_rprimKey (resourceInstanceId, resourceId) ) ENGINE = InnoDB, CHARACTER SET = utf8, COLLATE = utf8_unicode_ci;
CREATE TABLE IF NOT EXISTS ResourceInstance_ResourceInstancePermission ( id INT UNSIGNED NOT NULL AUTO_INCREMENT UNIQUE PRIMARY KEY, resourceInstanceResourceId INT UNSIGNED NOT NULL, resourcePrimKey INT UNSIGNED NOT NULL, permissions INT UNSIGNED NOT NULL, CONSTRAINT fk_resinsresinsperm_ririd_rir_id FOREIGN KEY (resourceInstanceResourceId) REFERENCES ResourceInstance_Resource (id) ON DELETE CASCADE ON UPDATE CASCADE, CONSTRAINT uq_resinsresinsperm_ririd_rprimKey UNIQUE uq_resinsresinsperm_ririd_rprimKey (resourceInstanceResourceId, resourcePrimKey) ) ENGINE = InnoDB, CHARACTER SET = utf8, COLLATE = utf8_unicode_ci;
CREATE TABLE IF NOT EXISTS Permission ( id INT UNSIGNED NOT NULL AUTO_INCREMENT UNIQUE PRIMARY KEY, resourceId INT UNSIGNED NOT NULL, resourcePrimKey INT UNSIGNED NOT NULL, resource2Id INT UNSIGNED NOT NULL, resource2PrimKey INT UNSIGNED NOT NULL, permission INT UNSIGNED NOT NULL, CONSTRAINT fk_permission_rid_resource_id FOREIGN KEY (resourceId) REFERENCES Resource (id) ON DELETE CASCADE ON UPDATE CASCADE, CONSTRAINT fk_permission_r2id_resource_id FOREIGN KEY (resourceId) REFERENCES Resource (id) ON DELETE CASCADE ON UPDATE CASCADE, CONSTRAINT uq_permission_rid_rprim_r2id_r2prim UNIQUE uq_permission_rid_rprim_r2id_r2prim (resourcePrimKey, resource2PrimKey, resourceId, resource2Id) ) ENGINE = InnoDB, CHARACTER SET = utf8, COLLATE = utf8_unicode_ci;


